                                     * @date        2016-12-12 00:10:06
                                     */
                                    if (isset($paramData[$metaDataCode . '.' . $row['META_DATA_CODE']][$parentRowId][$rowId])) {
                                        $paramValue = $paramData[$metaDataCode . '.' . $row['META_DATA_CODE']][$parentRowId][$rowId];                                        
