                        $tabItem[] = '<li><a href="#'.$tabId.'" data-toggle="tab" class="nav-link'.($t == 1 ? ' active' : '').'">'.Lang::line($tabName).'</a></li>';
                        
                        $tabPane[] = '<div class="tab-pane'.($t == 1 ? ' active' : '').'" id="'.$tabId.'">';
                        
