            }
        }
        
        $this->view->layout = $layout;
        
        if (self::$fieldCssStyle) {
            $this->view->gridHeaderClass .= self::$fieldCssStyle;
        }
        
        $renderProcess = $this->view->renderPrint('sub/layout/index', self::$viewPath);   

